import styled from "styled-components";
import {backGroundColor} from "../css/color";

export const SymbolButton = styled.button`
  background: none;
  font-size: 17px!important;
  padding-left: 12px;
  padding-right: 12px;
  outline: none;
  border: 2px solid transparent!important;
  color: ${backGroundColor(1)};
  padding-bottom: 4px;
  cursor: pointer;
  background-color: ${backGroundColor(0.1)};
  border-radius: 2px;
  transition: all 0.15s;
  box-sizing: unset!important;
  &:hover,focus {
    border: 2px solid ${backGroundColor(0.4)}!important;
  }
  &:active{
    border: 2px solid ${backGroundColor(0.2)}!important;
  }
`
